void straight_insertion_sort(T* arr, const int length) {
    // 外循环：遍历所有要被排序的元素
    for (int i = 1; i &lt; length; i++) {
        T tmp = arr[i]; // 取出待排序的元素
        int j = i - 1; // 向左寻找的起始索引
        // 内循环：向左边寻找待排序元素应该的位置
        while (j &gt;= 0 &amp;&amp; tmp &lt; arr[j]) { // 到该插入时停止内循环(1)
            arr[j + 1] = arr[j]; // 右移
            j--;
        }
        arr[j + 1] = tmp; // 插入
    }
}
